const BasicForm = (props) => {

const {value: nameValue, inputHasEror:nameInpuHasError, valueIsValid: nameIsValid, inputHandler: nameHandler, inputBlur: nameInputBlur,  ressetInput: resetNameInput  } = useNewInput(value => value.trim() !== '');

const formSubmitHandler = (e) => {
  e.preventDefault();
  console.log(nameValue);
  resetNameInput();
}
 let input
  return (
    <form onSubmit={formSubmitHandler}>
      <div className='control-group'>
        <div className='form-control'>
          <label htmlFor='name'>First Name</label>
          <input type='text' id='name' onBlur={nameInputBlur} onChange={nameHandler} value={nameValue} />
          {nameInpuHasError && <span className={['error-text']}>Enter Your Name</span>}
        </div>

        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' />
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' />
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
